mapper.select:
SELECT t.id
FROM CardTransaction t
         JOIN Card c ON t.card_id = c.id
WHERE c.user_id = #{userId}
  AND t.card_id = #{cardId}
  AND t.res_used_date = #{resUsedDate}
  AND t.res_used_time = #{resUsedTime}

SELECT EXISTS (
    SELECT 1
    FROM CardTransaction t
             JOIN Card c ON t.card_id = c.id
    WHERE t.id = #{transactionId}
      AND c.user_id = #{userId}
)
